import React, { useState, useEffect } from 'react';
import { productsAPI, attributesAPI } from '../services/api';

const Dashboard = () => {
  const [products, setProducts] = useState([]);
  const [attributes, setAttributes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddProduct, setShowAddProduct] = useState(false);
  const [showAddAttribute, setShowAddAttribute] = useState(false);
  const [newProduct, setNewProduct] = useState({
    name: '',
    title: '',
    price: '',
    status: 'active',
    attributes: []
  });
  const [newAttribute, setNewAttribute] = useState({
    name: '',
    options: ['']
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [productsRes, attributesRes] = await Promise.all([
        productsAPI.getProducts(),
        attributesAPI.getAttributes()
      ]);
      setProducts(productsRes.data.data);
      setAttributes(attributesRes.data.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      await productsAPI.createProduct(newProduct);
      setNewProduct({
        name: '',
        title: '',
        price: '',
        status: 'active',
        attributes: []
      });
      setShowAddProduct(false);
      fetchData();
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const handleAddAttribute = async (e) => {
    e.preventDefault();
    try {
      await attributesAPI.createAttribute(newAttribute);
      setNewAttribute({
        name: '',
        options: ['']
      });
      setShowAddAttribute(false);
      fetchData();
    } catch (error) {
      console.error('Error adding attribute:', error);
    }
  };

  const handleDeleteProduct = async (id) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      try {
        await productsAPI.deleteProduct(id);
        fetchData();
      } catch (error) {
        console.error('Error deleting product:', error);
      }
    }
  };

  const handleDeleteAttribute = async (id) => {
    if (window.confirm('Are you sure you want to delete this attribute?')) {
      try {
        await attributesAPI.deleteAttribute(id);
        fetchData();
      } catch (error) {
        console.error('Error deleting attribute:', error);
      }
    }
  };

  const addAttributeOption = () => {
    setNewAttribute({
      ...newAttribute,
      options: [...newAttribute.options, '']
    });
  };

  const updateAttributeOption = (index, value) => {
    const options = [...newAttribute.options];
    options[index] = value;
    setNewAttribute({
      ...newAttribute,
      options
    });
  };

  const removeAttributeOption = (index) => {
    const options = newAttribute.options.filter((_, i) => i !== index);
    setNewAttribute({
      ...newAttribute,
      options
    });
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="dashboard">
      <h1>Admin Dashboard</h1>

      <div className="dashboard-sections">
        {/* Products Section */}
        <div className="dashboard-section">
          <div className="section-header">
            <h2>Products ({products.length})</h2>
            <button 
              onClick={() => setShowAddProduct(true)}
              className="btn-primary"
            >
              Add Product
            </button>
          </div>

          <div className="products-grid">
            {products.map(product => (
              <div key={product._id} className="product-card">
                <h3>{product.name}</h3>
                <p>{product.title}</p>
                <p className="price">${product.price}</p>
                <p className={`status ${product.status}`}>
                  {product.status}
                </p>
                <div className="product-actions">
                  <button 
                    onClick={() => handleDeleteProduct(product._id)}
                    className="btn-danger"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Attributes Section */}
        <div className="dashboard-section">
          <div className="section-header">
            <h2>Attributes ({attributes.length})</h2>
            <button 
              onClick={() => setShowAddAttribute(true)}
              className="btn-primary"
            >
              Add Attribute
            </button>
          </div>

          <div className="attributes-grid">
            {attributes.map(attribute => (
              <div key={attribute._id} className="attribute-card">
                <h3>{attribute.name}</h3>
                <div className="attribute-options">
                  {attribute.options.map((option, index) => (
                    <span key={index} className="option-tag">
                      {option}
                    </span>
                  ))}
                </div>
                <div className="attribute-actions">
                  <button 
                    onClick={() => handleDeleteAttribute(attribute._id)}
                    className="btn-danger"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Add Product Modal */}
      {showAddProduct && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Add New Product</h3>
            <form onSubmit={handleAddProduct}>
              <div className="form-group">
                <label>Name</label>
                <input
                  type="text"
                  value={newProduct.name}
                  onChange={(e) => setNewProduct({...newProduct, name: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Title</label>
                <input
                  type="text"
                  value={newProduct.title}
                  onChange={(e) => setNewProduct({...newProduct, title: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Price</label>
                <input
                  type="number"
                  step="0.01"
                  value={newProduct.price}
                  onChange={(e) => setNewProduct({...newProduct, price: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Status</label>
                <select
                  value={newProduct.status}
                  onChange={(e) => setNewProduct({...newProduct, status: e.target.value})}
                >
                  <option value="active">Active</option>
                  <option value="inactive">Inactive</option>
                </select>
              </div>
              <div className="modal-actions">
                <button type="submit" className="btn-primary">Add Product</button>
                <button 
                  type="button" 
                  onClick={() => setShowAddProduct(false)}
                  className="btn-secondary"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Add Attribute Modal */}
      {showAddAttribute && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Add New Attribute</h3>
            <form onSubmit={handleAddAttribute}>
              <div className="form-group">
                <label>Attribute Name</label>
                <input
                  type="text"
                  value={newAttribute.name}
                  onChange={(e) => setNewAttribute({...newAttribute, name: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Options</label>
                {newAttribute.options.map((option, index) => (
                  <div key={index} className="option-input">
                    <input
                      type="text"
                      value={option}
                      onChange={(e) => updateAttributeOption(index, e.target.value)}
                      required
                    />
                    {newAttribute.options.length > 1 && (
                      <button 
                        type="button"
                        onClick={() => removeAttributeOption(index)}
                        className="btn-danger"
                      >
                        Remove
                      </button>
                    )}
                  </div>
                ))}
                <button 
                  type="button"
                  onClick={addAttributeOption}
                  className="btn-secondary"
                >
                  Add Option
                </button>
              </div>
              <div className="modal-actions">
                <button type="submit" className="btn-primary">Add Attribute</button>
                <button 
                  type="button" 
                  onClick={() => setShowAddAttribute(false)}
                  className="btn-secondary"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
